class App extends Component {

    constructor(props) {
        super(props)


        this.state = {
            clock: 1000,
            copo: 'água'
        }
    }

    alterarCopo = () => {
        this.setState({
            copo: 'Refrigerante'
        })
    }

    render() {

        const { clock, copo} = this.state

        return (
            <div>
                <h1>{clock}</h1>
                <button onClick={() => this.alterarCopo()}>{copo}</button>
            </div>
        )
    }

}
)
